import React, { Component } from "react";
import ReactDOM from "react-dom";
import SeatPicker from "react-seat-picker";
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';

let rows=[]
const options = [
  'one', 'two', 'three'
];
const defaultOption = options[0];


class Square extends Component {
  state = {
    loading: false,
  }

  createRows= (row, seats) =>{
    rows=[]
    for(var i=0;i<row;++i){
        let newSeats=[]
        for(var j=0;j<seats;++j){
            newSeats.push({ id: i*seats+j, number: i*seats+j})
        }
        rows.push(newSeats)
    }
  }

  render() {
      this.createRows(100,100);
      console.log(rows);
  
    const { loading } = this.state;
    return (
      <div class="horizontal-scroll-wrapper squares">
        <h1>Stadium Seats</h1>
        <Dropdown options={options} onChange={this._onSelect} value={defaultOption} placeholder="Select an option" className="dropDown">Stadiums</Dropdown>;
        <div style={{ marginTop: "100px" }}>
          <SeatPicker
            rows={rows}
            maxReservableSeats={1000}
            selectedByDefault={true}
          />
       
        </div>
      </div>
      
    );
  }
}

export default Square;
